INSERT INTO `categories` (`id`, `title`, `description`, `last_author`, `pubdate`) VALUES
(10, 'Архитектура', 'Несмотря на сложную историю, полную войн и разрушений, многие архитектурные ценности Беларуси сохранились. Наиболее древние сооружения в Беларуси относят к средневековью. Поэтому архитектура Беларуси является истинным свидетельством ее богатой истории.', NULL, '2019-11-11 14:51:36'),
(11, 'Природа', 'Природа Беларуси крайне привлекательна. Здесь можно встретить много редких видов растений и животных. В республике реализуются крупные природоохранные проекты, созданы государственные заповедники и заказники.', NULL, '2019-11-11 14:51:36'),
(18, 'Изобразительное искусство', 'фильмы', 'admin', '2019-11-23 19:53:25'),
(19, 'Литература', 'книги', 'admin', '2019-11-23 19:53:53');

